import { Component, Input, OnDestroy, OnInit } from '@angular/core';
import { FormField } from 'src/app/common-module/dto/form-field.dto';
import { CommonService } from 'src/app/common-module/services/common/common.service';
import { UrlService } from 'src/app/common-module/services/url/url.service';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-preview',
  templateUrl: './preview.component.html',
  styleUrls: ['./preview.component.css']
})
export class PreviewComponent implements OnInit, OnDestroy {
  @Input() formField!: FormField;
  @Input() data!: any;
  fallback = 'data:image/png;base64,';
  __data!: string;

  private __oldData?: any;
  private uuid = uuidv4().toLocaleLowerCase();
  __canPreview: boolean = true;
  resourceType?: 'img' | 'video' | 'web' | 'json';

  constructor(private commonService: CommonService, private urlService: UrlService) { }

  ngOnInit(): void {
    this.__oldData = this.data;
    this.tryConvertData();

    this.commonService.regInterval(this.uuid, () => {
      if (this.__oldData !== this.data) {
        this.__oldData = this.data;
        this.tryConvertData();
      }
    }, 100);
  }

  ngOnDestroy(): void {
    this.commonService.clearInterval(this.uuid);
  }

  private tryConvertData() {
    this.resourceType = this.formField.resourceType;
    if (this.formField.resourceType === 'json') {
      try {
        this.__data = JSON.parse(this.data);
        this.__canPreview = true;
      } catch (e) {
        this.__canPreview = false;
      }
    } else {
      let _arr = this.data.split('.');
      this.__canPreview = !UrlService.OFFICE_EXT.includes(_arr[_arr.length - 1]);
      if (this.__canPreview === false) return;

      if (this.formField.tryPreview === true && this.formField.code && this.data) {
        if (this.data.includes('www.youtube.com')) {
          this.resourceType = 'video';
        } else {
          let isImg: boolean = false;
          UrlService.IMG_EXT.forEach((ext: string) => {
            isImg = isImg || this.data.endsWith(ext);
          })
          if (isImg) {
            this.resourceType = 'img';
          }
        }
        if (this.resourceType === 'video') {
          this.__data = this.urlService.tryParseVideoLink(this.data);
        } else {
          this.__data = this.urlService.tryParseLinkURL(this.data);
        }
      }
    }
  }

}
